// src/SearchBar.js
import React, { useState, useEffect } from 'react';
import './SearchBar.css';  // Create a CSS file for styling
import jsonData from './data.json';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [countries, setCountries] = useState([]);
  const [filteredCountries, setFilteredCountries] = useState([]);

  // Fetch countries data from the JSON URL
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        //const response = await fetch('https://dpaste.com/79QXDY8TD/');
        const data = jsonData;
        console.log("ritu",data);
        setCountries(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchCountries();
  }, []);

  // Update filtered countries based on the search term
  useEffect(() => {
    if (searchTerm) {
      const results = countries.filter((country) =>
        country.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        country.capital.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCountries(results);
    } else {
      setFilteredCountries([]);
    }
  }, [searchTerm, countries]);

  return (
    <div className="search-container">
      <h1>Country Search</h1>
      <input
        type="text"
        className="search-input"
        placeholder="Search by country or capital..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul className="suggestions-list">
        {filteredCountries.map((country) => (
          <li key={country.name}>
            <strong>{country.name}</strong> - {country.capital}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
